void print(char *msg)
{
	if(msg != (void *) 0) write(1,msg,strlen(msg));
}

void wait(int ms)
{
  int init_time = gettime();
  int curr_time = gettime();

  while(1){
    if (curr_time >= init_time+ms) return;
    curr_time = gettime();
  }
}

void clear_screen()
{
	gotoXY(0,0);
	SetColor(2,0);
	int i, j;
	for (i = 0; i < NUM_ROWS; i++){	
		for (j = 0; j < NUM_COLUMNS; j ++) { 
			print(" ");
		}		
		print("\n");
	}
}


void print_Screens(int opt) { /*1-SplashScreen // 2-Map // 3-LoseScreen*/
  gotoXY(0,0);
  char ch[2];
  int i, j;
  for (i = 0; i < NUM_ROWS; i++){	
		for (j = 0; j < NUM_COLUMNS; j ++) { 
      switch(opt){
        case 1:
          //SetColor();
          ch[0] = splashScreen[i][j];
          ch[1] = '\0';
          break;
        case 2:
          //SetColor();
          ch[0] = map[i][j];
          ch[1] = '\0';
          break;
        case 3:
          //SetColor();
          ch[0] = loseScreen[i][j];
          ch[1] = '\0';
          break;
        case 4:
          //SetColor();
          ch[0] = winScreen[i][j];
          ch[1] = '\0';
          break;  
      }
			print(ch);
		}		
		print("\n");
	}
}

