void print(char *msg)
{
	if(msg != (void *) 0) write(1,msg,strlen(msg));
}

void wait(int sec)
{
  int init_time = gettime();
  int curr_time = gettime();

  while(1){
    if (curr_time >= init_time+sec*18) return;
    curr_time = gettime();
  }
}

void clear_screen()
{
	gotoXY(0,0);
	SetColor(2,0);
	int i, j;
	for (i = 0; i < NUM_ROWS; i++){	
		for (j = 0; j < NUM_COLUMNS; j ++) { 
			print(" ");
		}		
		print("\n");
	}
}


void print_Screens(int opt) { /*1-SplashScreen // 2-Map // 3-LoseScreen*/
  gotoXY(0,0);
  char ch;
  int i, j;
  for (i = 0; i < NUM_ROWS; i++){	
		for (j = 0; j < NUM_COLUMNS-1; j ++) { 
      switch(opt){
        case 1:
          //SetColor();
          ch = splashScreen[i][j];
          break;
        case 2:
          //SetColor();
          ch = map[i][j];
          break;
        case 3:
          //SetColor();
          ch = loseScreen[i][j];
          break;
        case 4:
          //SetColor();
          ch = winScreen[i][j];
          break;  
      }
			write(1,&ch,1);
		}		
		write(1,"\n",1);
	}
}

