void pacman_clean(int x, int y)
{
  for(int i = x; i < x+4; i++){
    for(int j = y; j < y+4; j++){
      map[j][i] = " ";
    }
  }
  print_Screens(2);
}

void coin_clean(int c)
{
	int x, y;
	if(c >= coin1.x && c <= coin1.x+4) {
		x = coin1.x;
		y = coin1.y;
	} else if (c >= coin2.x && c <= coin2.x+4) {
                x = coin2.x;
                y = coin2.y;
        } else if(c >= coin3.x && c <= coin3.x+4) {
                x = coin3.x;
                y = coin3.y;
        }

  for(int i = x; i < x+3; i++){
    for(int j = y; j < y+3; j++){
      map[j][i] = " ";
    }
  }
  print_Screens(2);
}

int check_ph_contact(int n_x, int n_y)
{
  for (int i = 0; i < 5; i++) {
	  if (map[n_y + i][n_x] == '0' || map[n_y + i][n_x + 4] == '0') return 1;    
    if (map[n_y][n_x + i] == '0' || map[n_y + 4][n_x + i] == '0') return 1;
  }
  return 0;
}

int check_coin_contact(int n_x, int n_y)
{
  for (int i = 0; i < 5; i++) {
	 if (map[n_y + i][n_x] == 'o') return n_x;
	 if (map[n_y + i][n_x + 4] == 'o') return n_x+4;
	 if (map[n_y][n_x + i] == 'o') return n_x+i;
	 if (map[n_y + 4][n_x + i] == 'o') return n_x+i;
  }
 
  return 0; 
}

int check_borders(int n_x, int n_y)
{
  //No pasarà nunca 
  if (n_x < 0 || n_y < 0 || n_x + 4 >= NUM_COLUMNS || n_y + 4 >= NUM_ROWS) return 1;
  
  for (int i = 0; i < 5; i++) {
	  if (map[n_y + i][n_x] == 'X' || map[n_y + i][n_x + 4] == 'X') return 1;
    if (map[n_y][n_x + i] == 'X' || map[n_y + 4][n_x + i] == 'X') return 1;
  }
  return 0;
}

int check_next_pos(int n_dir, int x, int y)
{
  //Calculo nueva pos 
  int n_x = x + directions[n_dir].x; 
  int n_y = y + directions[n_dir].y; 

  if (check_borders(n_x, n_y)) {
	  return 0;
  }
  //check phantom contact
  if (check_ph_contact(n_x, n_y)) {
    lose = 1;
    return 0;
  }

  //Check obtencion monedas
  int c = check_coin_contact(n_x, n_y);
  if(c != 0) {
	++num_coins;
	coin_clean(c);
  
  }

  //Habria que borrar el sprite ante de cambiar la pos
  pacman_clean(pos_pcm.x, pos_pcm.y);
  
  //El movimiento es correcto
  pos_pcm.x = n_x;
  pos_pcm.y = n_y;

  return 1;
}


